/*
  # Add recyclers from PDF document

  1. Changes
    - Insert recyclers from the PDF document
    - Add various recycling companies across South Africa
    - Include contact details and materials accepted
*/

-- Insert recyclers from the PDF document

-- Add service offerings, container types, and other details to the newly added recyclers
UPDATE recyclers
SET 
  service_offerings = ARRAY['Collection', 'Drop-off', 'Container Rental'],
  container_types = ARRAY['Bags (100L)', '240L Bins', '6m³ Skips', '12m³ Skips'],
  minimum_weight = 100.0,
  payment_terms = 'Payment on collection/delivery',
  operating_hours = 'Mon-Fri: 08:00-17:00, Sat: 08:00-13:00'
WHERE name IN ('Unica', 'Abacus', 'Abbys Machines', 'Africhair', 'Afripipe', 'Alex Plastics');

-- Update some recyclers with different service offerings and container types
UPDATE recyclers
SET 
  service_offerings = ARRAY['Drop-off', 'Industrial Services'],
  container_types = ARRAY['Bags (100L)', '240L Bins'],
  minimum_weight = 50.0,
  payment_terms = 'EFT within 24 hours',
  operating_hours = 'Mon-Fri: 07:30-16:30'
WHERE name IN ('Allsure Traders', 'Alrette', 'Alurite Services', 'AM Recycling', 'Anchor Recycling');

-- Update some recyclers with different minimum weights
UPDATE recyclers
SET 
  minimum_weight = 200.0
WHERE name IN ('AA Recycling Group', 'Antiwaste', 'APR Recycling', 'APR1', 'B4 Plastics');

-- Update verification status for some recyclers
UPDATE recyclers
SET 
  verified = false
WHERE name IN ('Bantjes Recycling', 'BB Plastics', 'Belltone Trading', 'BPCR Plastics', 'Bretcor');